// Extract the message data from YCloud webhook
const data = $input.item.json.body;

// Check if this is a WhatsApp message
if (data && data.event === 'message' && data.channel === 'whatsapp') {
  const message = data.content?.text || '';
  const sender = data.from || '';
  const messageId = data.id || '';
  const timestamp = data.createdAt || new Date().toISOString();

  return {
    json: {
      messageId,
      sender,
      message,
      timestamp,
      isProcessed: false,
      processingMethod: null,
      transactionData: null
    }
  };
} else {
  // Not a WhatsApp message we want to process
  return {
    json: {
      skip: true,
      reason: 'Not a valid WhatsApp message'
    }
  };
}

// Pattern matching for transaction extraction
const message = $input.item.json.message;

// Define regex patterns for different transaction types
const patterns = {
  // Expense pattern: amount, category, vendor
  expense: /(?:spent|paid|expense|cost|bought|purchased|payment)\s+(?:of\s+)?\$?(\d+(?:\.\d{1,2})?)\s+(?:on|for|at|to)?\s+(?:([\w\s]+))?(?:\s+at\s+|\s+to\s+|\s+from\s+)?([\w\s]+)?/i,

  // Income pattern: amount, source
  income: /(?:received|earned|got|income|payment of)\s+(?:of\s+)?\$?(\d+(?:\.\d{1,2})?)\s+(?:from|by|via|through)?\s+([\w\s]+)?/i,

  // Transfer pattern: amount, from account, to account
  transfer: /(?:transferred|moved|sent)\s+(?:of\s+)?\$?(\d+(?:\.\d{1,2})?)\s+(?:from\s+([\w\s]+))?\s+(?:to\s+([\w\s]+))?/i
};

// Try to match the message against patterns
let transactionData = null;
let transactionType = null;
let matchFound = false;

// Check for expense pattern
const expenseMatch = message.match(patterns.expense);
if (expenseMatch) {
  const [_, amount, category, vendor] = expenseMatch;
  transactionData = {
    type: 'expense',
    amount: parseFloat(amount),
    category: category ? category.trim() : 'Uncategorized',
    vendor: vendor ? vendor.trim() : 'Unknown',
    date: new Date().toISOString().split('T')[0]
  };
  transactionType = 'expense';
  matchFound = true;
}

// Check for income pattern if no expense match
if (!matchFound) {
  const incomeMatch = message.match(patterns.income);
  if (incomeMatch) {
    const [_, amount, source] = incomeMatch;
    transactionData = {
      type: 'income',
      amount: parseFloat(amount),
      source: source ? source.trim() : 'Unknown',
      category: 'Income',
      date: new Date().toISOString().split('T')[0]
    };
    transactionType = 'income';
    matchFound = true;
  }
}

// Check for transfer pattern if no other matches
if (!matchFound) {
  const transferMatch = message.match(patterns.transfer);
  if (transferMatch) {
    const [_, amount, fromAccount, toAccount] = transferMatch;
    transactionData = {
      type: 'transfer',
      amount: parseFloat(amount),
      fromAccount: fromAccount ? fromAccount.trim() : 'Unknown',
      toAccount: toAccount ? toAccount.trim() : 'Unknown',
      date: new Date().toISOString().split('T')[0]
    };
    transactionType = 'transfer';
    matchFound = true;
  }
}

// Return the result
return {
  json: {
    ...$input.item.json,
    patternMatchAttempted: true,
    patternMatchSuccessful: matchFound,
    transactionType: transactionType,
    transactionData: transactionData,
    processingMethod: matchFound ? 'pattern_matching' : null,
    needsAiProcessing: !matchFound
  }
};

// Template matching for transaction extraction
const message = $input.item.json.message.toLowerCase();

// Define templates for common transaction formats
const templates = [
  // Template: "Grocery $45.30 Walmart"
  {
    regex: /(\w+)\s+\$?(\d+(?:\.\d{1,2})?)\s+([\w\s]+)/i,
    process: (matches) => {
      const [_, category, amount, vendor] = matches;
      return {
        type: 'expense',
        amount: parseFloat(amount),
        category: category.trim(),
        vendor: vendor.trim(),
        date: new Date().toISOString().split('T')[0]
      };
    }
  },
  // Template: "$50 gas station"
  {
    regex: /\$?(\d+(?:\.\d{1,2})?)\s+([\w\s]+)/i,
    process: (matches) => {
      const [_, amount, description] = matches;
      // Try to determine if this is a vendor or category
      const commonVendors = ['walmart', 'target', 'amazon', 'starbucks', 'mcdonalds', 'uber', 'lyft'];
      const commonCategories = ['groceries', 'food', 'gas', 'transportation', 'entertainment', 'utilities'];

      let category = 'Uncategorized';
      let vendor = 'Unknown';

      const words = description.toLowerCase().split(' ');

      // Check if any word matches common vendors
      for (const word of words) {
        if (commonVendors.includes(word)) {
          vendor = description.trim();
          break;
        }
      }

      // Check if any word matches common categories
      for (const word of words) {
        if (commonCategories.includes(word)) {
          category = word.charAt(0).toUpperCase() + word.slice(1);
          if (vendor === 'Unknown') {
            vendor = description.trim();
          }
          break;
        }
      }

      // If no category was found but vendor was, use a default category
      if (category === 'Uncategorized' && vendor !== 'Unknown') {
        category = 'General';
      }

      // If no vendor was found but category was, use description as vendor
      if (vendor === 'Unknown' && category !== 'Uncategorized') {
        vendor = description.trim();
      }

      return {
        type: 'expense',
        amount: parseFloat(amount),
        category: category,
        vendor: vendor,
        date: new Date().toISOString().split('T')[0]
      };
    }
  }
];

// Try to match the message against templates
let transactionData = null;
let matchFound = false;

for (const template of templates) {
  const match = message.match(template.regex);
  if (match) {
    transactionData = template.process(match);
    matchFound = true;
    break;
  }
}

// Return the result
return {
  json: {
    ...$input.item.json,
    templateMatchAttempted: true,
    templateMatchSuccessful: matchFound,
    transactionData: matchFound ? transactionData : $input.item.json.transactionData,
    processingMethod: matchFound ? 'template_matching' : $input.item.json.processingMethod,
    needsAiProcessing: !matchFound && $input.item.json.needsAiProcessing
  }
};

