// Extract the transaction data from the AI response
let transactionData = null;

try {
  // Parse the AI response
  const aiResponse = $input.item.json.body;
  const content = aiResponse.choices[0].message.content;

  // Extract the JSON object from the content
  transactionData = JSON.parse(content);

  // Add current date if not provided
  if (!transactionData.date) {
    transactionData.date = new Date().toISOString().split('T')[0];
  }

  return {
    json: {
      ...$input.item.json,
      aiProcessingAttempted: true,
      aiProcessingSuccessful: true,
      transactionData: transactionData,
      processingMethod: 'ai_processing',
      needsAiProcessing: false
    }
  };
} catch (error) {
  // Handle parsing errors
  return {
    json: {
      ...$input.item.json,
      aiProcessingAttempted: true,
      aiProcessingSuccessful: false,
      aiError: error.message,
      processingMethod: $input.item.json.processingMethod,
      needsAiProcessing: false
    }
  };
}

